static void
out_html(char *c)
{
	if (!c)
		return;
	if (no_newline_output) {
		int     i = 0;

		no_newline_output = 1;
		while (c[i]) {
			if (!no_newline_output)
				c[i - 1] = c[i];
			if (c[i] == '\n')
				no_newline_output = 1;
			i++;
		}
		if (!no_newline_output)
			c[i - 1] = 0;
	}
	if (scaninbuff) {
		while (*c) {
			if (buffpos >= buffmax) {
				char   *h;

				h = realloc(buffer, buffmax * 2);
				if (!h)
					return;
				buffer = h;
				buffmax *= 2;
			}
			buffer[buffpos++] = *c++;
		}
	} else if (output_possible) {
		while (*c) {
			outbuffer[obp++] = *c;
			if (*c == '\n' || obp > HUGE_STR_MAX) {
				outbuffer[obp] = '\0';
				add_links(outbuffer);
				obp = 0;
			}
			c++;
		}
	}
}
