call_match(int nargs)
{
	NODE *regex, *text, *array;
	NODE *result;

	regex = text = array = NULL;
	if (nargs == 3)
		array = POP();
	regex = POP();

	/* Don't need to pop the string just to push it back ... */

	regex = make_regnode(Node_regex, regex);
	PUSH(regex);

	if (array)
		PUSH(array);

	result = do_match(nargs);
	return result;
}
