wextglob_skipname (pat, dname, flags)
     wchar_t *pat, *dname;
     int flags;
{
#if EXTENDED_GLOB
  wchar_t *pp, *pe, *t, n;
  int r;

  pp = pat + 2;
  pe = pp + wcslen (pp) - 1;	/*(*/
  if (*pe != L')')
    return 0;
  if ((t = wcschr (pp, L'|')) == 0)
    {
      *pe = L'\0';
      r = wchkname (pp, dname); /*(*/
      *pe = L')';
      return r;
    }
  while (t = glob_patscan_wc (pp, pe, '|'))
    {
      n = t[-1];
      t[-1] = L'\0';
      r = wchkname (pp, dname);
      t[-1] = n;
      if (r == 0)
	return 0;
      pp = t;
    }

  if (pp == pe)		/* glob_patscan_wc might find end of pattern */
    return r;

  *pe = L'\0';
  r = wchkname (pp, dname);	/*(*/
  *pe = L')';
  return r;
#else
  return (wchkname (pat, dname));
#endif
}
