static int
snarf_char_class(b, cur_stat)
  struct buffer *b;
  mbstate_t *cur_stat;
{
  int ch;
  int state = 0;
  int delim;
  bool pending_mb = 0;

  ch = inchar();
  if (ch == '^')
    ch = add_then_next(b, ch);
  if (ch == CLOSE_BRACKET)
    ch = add_then_next(b, ch);

  /* States are:
	0 outside a collation element, character class or collation class
	1 after the bracket
	2 after the opening ./:/=
	3 after the closing ./:/= */

  for (;; ch = add_then_next (b, ch))
    {
      pending_mb = BRLEN (ch, cur_stat) != 1;

      switch (ch)
	{
	case EOF:
	case '\n':
	  return ch;

	case '.':
	case ':':
	case '=':
	  if (pending_mb)
	    continue;

	  if (state == 1)
	    {
	      delim = ch;
	      state++;
	    }
	  else if (state == 2 && ch == delim)
	    state++;
	  else
	    break;

	  continue;

	case OPEN_BRACKET:
	  if (pending_mb)
	    continue;

	  state++;
	  continue;

	case CLOSE_BRACKET:
	  if (pending_mb)
	    continue;

	  if (state == 0 || state == 1)
	    return ch;
	  else if (state == 3)
	    state = 0;

	  break;

	default:
	  break;
	}

      /* Getting a character different from .=: whilst in state 1
         goes back to state 0, getting a character different from ]
         whilst in state 3 goes back to state 2.  */
      state &= ~1;
    }
}
