static void _parse_host_and_port(char **host, char **port, char *default_port)
{
    char *_port = NULL;
    char *pos = *host;

    if (pos[0] == '[') {
        (*host)++;
        pos = strrchr(pos, ']');
    }

    _port = strrchr(pos, ':');
    if (!_port) {
        *pos = '\0';
        DEBUG("[_parse_port] No port specified, using default\n");
        _port = default_port;
    }
    else {
        *(_port - 1) = '\0';
        _port++;
    }
    *port = _port;
}
