public ArrayType(Type type, int dimensions) {
super(Const.T_ARRAY, "<dummy>");
if ((dimensions < 1) || (dimensions > Const.MAX_BYTE)) {
throw new ClassGenException("Invalid number of dimensions: " + dimensions);
}
switch(type.getType()) {
